'use strict';

var React = require('react')
var Tree  = require('./src')
var TreeNode = Tree.Node

require('./index.styl')

var items = require('./cities')

var romaniaCollapsed = true

var App = React.createClass({

    onChange: function(collapsed, data){
        data.collapsed = collapsed
        this.setState({})
    },

    collapseRomania: function(){
        romaniaCollapsed = !romaniaCollapsed

        this.setState({})
    },

    render: function() {

        // <Tree >
        //     <Tree.Node>North America</Tree.Node>
        //     <Tree.Node>
        //         Europe
        //         <Tree>
        //             <Tree.Node>France</Tree.Node>
        //             <Tree.Node>Romania</Tree.Node>
        //         </Tree>
        //     </Tree.Node>
        // </Tree>

        return (
            <div className="App" style={{padding: 20}}>
                <Tree datax={items} onChangex={this.onChange}>
                    <Tree.Node>North America</Tree.Node>
                    <Tree.Node>
                        Europe
                        <Tree>
                            <Tree.Node>
                                France
                                <Tree>
                                    <Tree.Node>Paris</Tree.Node>
                                    <Tree.Node>Lion</Tree.Node>
                                </Tree>
                            </Tree.Node>
                            <Tree.Node >Romania
                                <Tree>
                                    <Tree.Node>Cluj-Napoca</Tree.Node>
                                    <Tree.Node>Bucharest</Tree.Node>
                                </Tree>
                            </Tree.Node>
                        </Tree>
                    </Tree.Node>
                </Tree>
            </div>
        )
    }
})

React.render(<App />, document.getElementById('content'))